import React from 'react';


class PizzasList extends React.Component {
    state = {
        loading: true,
        article: null
    };

    async componentDidMount() {
        const url = "http://localhost:8000/api/articles";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({article: data.results[1], loading:false});
        console.log(data.results[0]);
    }

    render() {

        return (
            <div>
                {this.state.loading || !this.state.article ? 
                (<div>loading...</div> ) : (<div> {this.state.article.title}</div>)}
            </div>
        )
    }
}

export default PizzasList;
