import React, { useState } from 'react';

function Login(props) {
    const username = useFormInput('');
    const password = useFormInput('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    // handle button click of login form
    const handleLogin = () => {
        props.history.push('/dashboard');
    }

    return (
        <div className="d-flex flex-column justify-content-center">
            <h3>Login</h3><br /><br />
            <div>
                <label class="form-check-label" for="exampleCheck1">username</label><br />
                <input className="form-control" type="text" {...username} autoComplete="new-password" />
            </div>
            <div style={{ marginTop: 10 }}>
                <label class="form-check-label" for="exampleCheck1">Password</label><br />
                <input className="form-control" type="password" {...password} autoComplete="new-password" />
            </div>
            {error && <><small style={{ color: 'red' }}>{error}</small><br /></>}<br />
            <input type="button" value={loading ? 'Loading...' : 'Login'} onClick={handleLogin} disabled={loading} /><br />
            <p>Don't have an account?</p> <button>Register here!</button>
        </div>
    );
}

const useFormInput = initialValue => {
    const [value, setValue] = useState(initialValue);

    const handleChange = e => {
        setValue(e.target.value);
    }
    return {
        value,
        onChange: handleChange
    }
}

export default Login;
