import React from 'react';
import Axios from 'axios';
import { List, ListItem } from 'material-ui';


class PizzasList extends React.Component {
    constructor(){
        super();
        this.state={
            articles:[]
        }
    }

    componentDidMount(){
        Axios.get('http://localhost:8000/api/articles')
        .then(response=>{
            this.setState({articles: response.data})
        })
    }

    render() {

        return (
            <div>
                <h2><em>YummiPizza Menu:</em></h2>
                <ul className="list-group">
                    { this.state.articles.map(article=>{
                        return(
                            <div>
                            <li className="list-group-item  border mt-3 d-flex w-100 flex-xl-row flex-column justify-content-between">{article.title}
                            <div>
                            <p>{article.price}EUR</p>
                                    </div>
                            <button className="btn btn-info mt-3">Add to cart</button>

                            </li>
                                <p>({article.decription})</p>
                            </div>

                    )})}
                </ul>

            </div>

        )
    }
}

export default PizzasList;
